INSERT INTO `DDMTemplate` VALUES ('b4e11b3c-5840-dfc6-f1c4-6e814d3ddafb',30812,20135,20099,20103,NULL,20103,NULL,'2019-10-29 11:37:47.249000','2019-10-29 11:37:47.249000',28902,0,30802,'WIKI-SOCIAL-FTL','1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Sosiaalinen</Name><Name language-id=\"en_US\">Social</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Social</Name><Name language-id=\"iw_IL\"></Name><Name language-id=\"pt_BR\">Social</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Social</Name><Name language-id=\"ca_ES\">Social</Name><Name language-id=\"nl_NL\">Sociaal</Name><Name language-id=\"hu_HU\">Kzssgi</Name><Name language-id=\"de_DE\">Social</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt sosiaaliset kirjanmerkit ja arvostelut wikisivuilla ja niiden alisivuilla.</Description><Description language-id=\"en_US\">Displays social bookmarks and ratings for wiki pages and their child pages.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra los marcadores sociales y las clasificaciones para pginas wiki y sus pginas secundarias.</Description><Description language-id=\"iw_IL\">         .</Description><Description language-id=\"pt_BR\">Exibe marcadores sociais e avaliaes para pginas da wiki e suas pginas filhas.</Description><Description language-id=\"ja_JP\">Wiki</Description><Description language-id=\"fr_FR\">Affiche les signets sociaux and les notations pour page wiki et leur pages enfants.</Description><Description language-id=\"ca_ES\">Mostra adreces d\'inters socials i classificacions per a planes del wiki.</Description><Description language-id=\"nl_NL\">Geeft sociale favorieten en waarderingen van wikipagina\'s en onderliggende wikipagina\'s weer.</Description><Description language-id=\"hu_HU\">Megjelenti a kzssgi knyvjelzket s rtkelseket a wiki oldalak s gyermek oldalaik rszre.</Description><Description language-id=\"de_DE\">Darstellung fr Social Bookmarks und Bewertungen von Wikiseiten und deren Unterseiten.</Description></root>','display',NULL,'ftl','<#assign\n	wikiPageClassName = \"com.liferay.wiki.model.WikiPage\"\n\n	assetRenderer = assetEntry.getAssetRenderer()\n/>\n\n<div class=\"taglib-header\">\n	<h1 class=\"header-title\">${entry.getTitle()}</h1>\n</div>\n\n<div style=\"float: right;\">\n	<@getEditIcon />\n\n	<@getPageDetailsIcon />\n\n	<@getPrintIcon />\n</div>\n\n<div class=\"wiki-body\">\n	<div class=\"wiki-info\">\n		<span class=\"stats\">${assetEntry.getViewCount()} <@liferay.language key=\"views\" /></span> |\n\n		<span class=\"date\"><@liferay.language key=\"last-modified\" /> ${dateUtil.getDate(entry.getModifiedDate(), \"dd MMM yyyy - HH:mm:ss\", locale)}</span>\n\n		<span class=\"author\"><@liferay.language key=\"by\" /> ${htmlUtil.escape(portalUtil.getUserName(entry.getUserId(), entry.getUserName()))}</span>\n	</div>\n\n	<div class=\"wiki-content\">\n		<@liferay_social_bookmarks[\"bookmarks\"]\n			className=wikiPageClassName\n			classPK=entry.getResourcePrimKey()\n			displayStyle=\"normal\"\n			target=\"_blank\"\n			title=entry.getTitle()\n			url=viewURL\n		/>\n\n		${formattedContent}\n	</div>\n\n	<div class=\"page-actions\">\n		<div class=\"article-actions\">\n			<@getAddChildPageIcon />\n\n			<@getAttatchmentsIcon />\n		</div>\n	</div>\n\n	 <br />\n\n	<@getRatings\n		cssClass=\"page-ratings\"\n		entry=entry\n	/>\n\n	<@getRelatedAssets />\n</div>\n\n<div class=\"page-categorization\">\n	<div class=\"page-categories\">\n		<#assign viewCategorizedPagesURL = renderResponse.createRenderURL() />\n\n		${viewCategorizedPagesURL.setParameter(\"mvcRenderCommandName\", \"/wiki/view_categorized_pages\")}\n		${viewCategorizedPagesURL.setParameter(\"nodeId\", entry.getNodeId()?string)}\n\n		<@liferay_ui[\"asset-categories-summary\"]\n			className=wikiPageClassName\n			classPK=entry.getResourcePrimKey()\n			portletURL=viewCategorizedPagesURL\n		/>\n	</div>\n\n	<div class=\"page-tags\">\n		<#assign viewTaggedPagesURL = renderResponse.createRenderURL() />\n\n		${viewTaggedPagesURL.setParameter(\"mvcRenderCommandName\", \"/wiki/view_tagged_pages\")}\n		${viewTaggedPagesURL.setParameter(\"nodeId\", entry.getNodeId()?string)}\n\n		<@liferay_ui[\"asset-tags-summary\"]\n			className=wikiPageClassName\n			classPK=entry.getResourcePrimKey()\n			portletURL=viewTaggedPagesURL\n		/>\n	</div>\n</div>\n\n<#assign childPages = entry.getChildPages() />\n\n<#if (childPages?has_content)>\n	<div class=\"child-pages\">\n		<h2><@liferay.language key=\"children-pages\" /></h2>\n\n		<table class=\"taglib-search-iterator\">\n			<tr class=\"portlet-section-header results-header\">\n				<th>\n					<@liferay.language key=\"page\" />\n				</th>\n				<th>\n					<@liferay.language key=\"last-modified\" />\n				</th>\n				<th>\n					<@liferay.language key=\"ratings\" />\n				</th>\n			</tr>\n\n			<#list childPages as childPage>\n				<tr class=\"results-row\">\n					<#assign viewPageURL = renderResponse.createRenderURL() />\n\n					${viewPageURL.setParameter(\"mvcRenderCommandName\", \"/wiki/view\")}\n\n					<#assign childNode = childPage.getNode() />\n\n					${viewPageURL.setParameter(\"nodeName\", childNode.getName())}\n					${viewPageURL.setParameter(\"title\", childPage.getTitle())}\n\n					<td>\n						<a href=\"${viewPageURL}\">${childPage.getTitle()}</a>\n					</td>\n					<td>\n						<a href=\"${viewPageURL}\">${dateUtil.getDate(childPage.getModifiedDate(),\"dd MMM yyyy - HH:mm:ss\", locale)} <@liferay.language key=\"by\" /> ${htmlUtil.escape(portalUtil.getUserName(childPage.getUserId(), childPage.getUserName()))}</a>\n					</td>\n					<td>\n						<@getRatings\n							cssClass=\"child-ratings\"\n							entry=childPage\n						/>\n					</td>\n				</tr>\n			</#list>\n		</table>\n	</div>\n</#if>\n\n<@getDiscussion />\n\n<#macro getAddChildPageIcon>\n	<#if assetRenderer.hasEditPermission(themeDisplay.getPermissionChecker())>\n		<#assign addPageURL = renderResponse.createRenderURL() />\n\n		${addPageURL.setParameter(\"mvcRenderCommandName\", \"/wiki/edit_page\")}\n		${addPageURL.setParameter(\"redirect\", currentURL)}\n		${addPageURL.setParameter(\"nodeId\", entry.getNodeId()?string)}\n		${addPageURL.setParameter(\"title\", \"\")}\n		${addPageURL.setParameter(\"editTitle\", \"1\")}\n		${addPageURL.setParameter(\"parentTitle\", entry.getTitle())}\n\n		<@liferay_ui[\"icon\"]\n			iconCssClass=\"icon-plus\"\n			label=true\n			message=\"add-child-page\"\n			url=addPageURL?string\n		/>\n	</#if>\n</#macro>\n\n<#macro getAttatchmentsIcon>\n	<#assign viewPageAttachmentsURL = renderResponse.createRenderURL() />\n\n	${viewPageAttachmentsURL.setParameter(\"mvcRenderCommandName\", \"/wiki/view_page_attachments\") }\n\n	<@liferay_ui[\"icon\"]\n		iconCssClass=\"icon-paperclip\"\n		label=true\n		message=\'${entry.getAttachmentsFileEntriesCount() + languageUtil.get(locale, \"attachments\")}\'\n		url=viewPageAttachmentsURL?string\n	/>\n</#macro>\n\n<#macro getDiscussion>\n	<#if validator.isNotNull(assetRenderer.getDiscussionPath()) && wikiPortletInstanceConfiguration.enableComments()>\n		<br />\n\n		<#assign discussionURL = renderResponse.createActionURL() />\n\n		${discussionURL.setParameter(\"javax.portlet.action\", \"/wiki/\" + assetRenderer.getDiscussionPath())}\n\n		<@liferay_comment[\"discussion\"]\n			className=wikiPageClassName\n			classPK=entry.getResourcePrimKey()\n			formAction=discussionURL?string\n			formName=\"fm2\"\n			ratingsEnabled=wikiPortletInstanceConfiguration.enableCommentRatings()\n			redirect=currentURL\n			subject=assetRenderer.getTitle(locale)\n			userId=assetRenderer.getUserId()\n		/>\n	</#if>\n</#macro>\n\n<#macro getEditIcon>\n	<#if assetRenderer.hasEditPermission(themeDisplay.getPermissionChecker())>\n		<#assign editPageURL = renderResponse.createRenderURL() />\n\n		${editPageURL.setParameter(\"mvcRenderCommandName\", \"/wiki/edit_page\")}\n		${editPageURL.setParameter(\"redirect\", currentURL)}\n		${editPageURL.setParameter(\"nodeId\", entry.getNodeId()?string)}\n		${editPageURL.setParameter(\"title\", entry.getTitle())}\n\n		<@liferay_ui[\"icon\"]\n			iconCssClass=\"icon-edit\"\n			message=entry.getTitle()\n			url=editPageURL?string\n		/>\n	</#if>\n</#macro>\n\n<#macro getPageDetailsIcon>\n	<#assign viewPageDetailsURL = renderResponse.createRenderURL() />\n\n	${viewPageDetailsURL.setParameter(\"mvcRenderCommandName\", \"/wiki/view_page_details\")}\n	${viewPageDetailsURL.setParameter(\"redirect\", currentURL)}\n\n	<@liferay_ui[\"icon\"]\n		iconCssClass=\"icon-file-alt\"\n		message=\"details\"\n		url=viewPageDetailsURL?string\n	/>\n</#macro>\n\n<#macro getPrintIcon>\n	<#assign printURL = renderResponse.createRenderURL() />\n\n	${printURL.setParameter(\"viewMode\", \"print\")}\n	${printURL.setWindowState(\"pop_up\")}\n\n	<#assign\n		title = languageUtil.format(locale, \"print-x-x\", [\"hide-accessible\", htmlUtil.escape(assetRenderer.getTitle(locale))], false)\n		taglibPrintURL = \"javascript:Liferay.Util.openWindow({dialog: {width: 960}, id:\'\" + renderResponse.getNamespace() + \"printAsset\', title: \'\" + title + \"\', uri: \'\" + htmlUtil.escapeURL(printURL.toString()) + \"\'});\"\n	/>\n\n	<@liferay_ui[\"icon\"]\n		iconCssClass=\"icon-print\"\n		message=\"print\"\n		url=taglibPrintURL\n	/>\n</#macro>\n\n<#macro getRatings\n	cssClass\n	entry\n>\n	<#if wikiPortletInstanceConfiguration.enablePageRatings()>\n		<div class=\"${cssClass}\">\n			<@liferay_ui[\"ratings\"]\n				className=wikiPageClassName\n				classPK=entry.getResourcePrimKey()\n			/>\n		</div>\n	</#if>\n</#macro>\n\n<#macro getRelatedAssets>\n	<#if assetEntry?? && wikiPortletInstanceConfiguration.enableRelatedAssets()>\n		<@liferay_ui[\"asset-links\"] assetEntryId=assetEntry.getEntryId() />\n	</#if>\n</#macro>',0,0,30813,NULL,NULL);
INSERT INTO `DDMTemplate` VALUES ('3f7f11d3-ae4d-56f9-82fd-876395c7ea0a',30816,20135,20099,20103,NULL,20103,NULL,'2019-10-29 11:37:47.284000','2019-10-29 11:37:47.284000',30815,0,30802,'RSS-NAVIGATION-FTL','1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Navigointi</Name><Name language-id=\"en_US\">Navigation</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Navegacin</Name><Name language-id=\"iw_IL\"></Name><Name language-id=\"pt_BR\">Navegao</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Navigation</Name><Name language-id=\"ca_ES\">Navegaci</Name><Name language-id=\"nl_NL\">Navigatie</Name><Name language-id=\"hu_HU\">Navigci</Name><Name language-id=\"de_DE\">Navigation</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt RSS-sytteet navigaatiopalkissa vasemmassa paneelissa. Klikkaamalla sytett nytetn sytteen arvot oikeassa paneelissa.</Description><Description language-id=\"en_US\">Displays the RSS feeds as a navigation bar on the left panel. Clicking on a feed displays a list of feed entries on the right panel.</Description><Description language-id=\"zh_CN\">RSSFeed</Description><Description language-id=\"es_ES\">Muestra las fuentes RSS como una barra de navegacin del panel izquierdo. Al hacer clic en una fuente se muestra una lista de entradas de la fuente en el panel derecho.</Description><Description language-id=\"iw_IL\">   -RSS    .          .</Description><Description language-id=\"pt_BR\">Exibe os feeds RSS como uma barra de navegao no painel esquerdo. Clicar em um feed exibe uma lista de entradas de feed no painel direito.</Description><Description language-id=\"ja_JP\">RSS</Description><Description language-id=\"fr_FR\">Affiche les flux RSS en tant que barre de navigation dans le panneau gauche. Cliquer sur un flux affiche une liste d\'entres de flux sur le panneau droit.</Description><Description language-id=\"ca_ES\">Mostra els canals RSS com una barra de navegaci en el tauler esquerra. Al fer un clic en un canal es mostraran la llista d\'entrades en el tauler dret.</Description><Description language-id=\"nl_NL\">Geeft de RSS-feeds als een navigatiebalk in het linkerpaneel weer. Als u op een feed klikt wordt een lijst van van feed-entries in het rechterpaneel getoond.</Description><Description language-id=\"hu_HU\">Megjelenti az RSS hrcsatornkat navigcis oszlopknt a bal oldalon. Egy hrcsatornra kattintva megjelenti a hrcsatorna bejegyzsek listjt a jobb oldalon.</Description><Description language-id=\"de_DE\">Stellt die RSS-Feeds als Navigationsleiste im linken Bereich dar. Nach Anklicken eines Feeds werden die entsprechenden Feedeintrge im rechten Bereich dargestellt.</Description></root>','display',NULL,'ftl','<#assign rssPortletInstanceConfiguration = rssDisplayContext.getRSSPortletInstanceConfiguration() />\n\n<style>\n	.portlet-rss .feed-entry-content {\n		margin-bottom: 20px;\n		padding-left: 0;\n	}\n</style>\n\n<#if entries?has_content>\n	<#assign dateFormat = \"dd MMM yyyy - HH:mm:ss\" />\n\n	<div class=\"container-fluid\">\n		<div class=\"row\" id=\"<@portlet.namespace />feedsTab\">\n			<ul class=\"col-xs-4\">\n				<#list entries as curEntry>\n					<li><a href=\"#tab-${curEntry_index}\">${htmlUtil.escape(curEntry.getTitle())}</a></li>\n				</#list>\n			</ul>\n\n			<div class=\"col-xs-8 tab-content\">\n				<#list entries as curEntry>\n					<#assign rssFeedEntries = curEntry.getRSSFeedEntries(themeDisplay) />\n\n					<#if rssFeedEntries??>\n						<div class=\"tab-pane\" id=\"tab-${curEntry_index}\">\n							<#list rssFeedEntries as rssFeedEntry>\n								<#if (rssFeedEntry_index > entriesPerFeed?number)>\n									<#break>\n								</#if>\n\n								<#assign syndEntry = rssFeedEntry.getSyndEntry() />\n\n								<div class=\"feed-entry-content\">\n									<div class=\"feed-title\">\n										<@liferay_aui[\"a\"] href=\"${htmlUtil.escapeJSLink(rssFeedEntry.getSyndEntryLink())}\">${htmlUtil.escape(syndEntry.getTitle())}</@>\n									</div>\n\n									<#if rssPortletInstanceConfiguration.showFeedTitle() && syndEntry.getAuthor()??>\n										<div class=\"feed-entry-author\">\n											${htmlUtil.escape(syndEntry.getAuthor())}\n										</div>\n									</#if>\n\n									<#if syndEntry.getPublishedDate()??>\n										<div class=\"feed-date\">\n											<@liferay_ui[\"icon\"]\n												iconCssClass=\"icon-calendar\"\n												label=true\n												message=\"${dateUtil.getDate(syndEntry.getPublishedDate(), dateFormat, locale)}\"\n											/>\n										</div>\n									</#if>\n\n									${rssFeedEntry.getSanitizedContent()}\n								</div>\n							</#list>\n						</div>\n					</#if>\n				</#list>\n			</div>\n		</div>\n	</div>\n\n	<@liferay_aui[\"script\"] use=\"aui-base,aui-tabview\">\n		new A.TabView(\n			{\n				srcNode: \'#<@portlet.namespace />feedsTab\',\n				stacked: true,\n				type: \'pills\'\n			}\n		).render();\n	</@>\n</#if>',0,0,30817,NULL,NULL);
INSERT INTO `DDMTemplate` VALUES ('702afce2-5bb9-5f57-2192-058dd95f93c3',30820,20135,20099,20103,NULL,20103,NULL,'2019-10-29 11:37:47.321000','2019-10-29 11:37:47.321000',30819,0,30802,'LIST-MENU-FTL','1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Luettelovalikko</Name><Name language-id=\"en_US\">List Menu</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Men de lista</Name><Name language-id=\"iw_IL\"> </Name><Name language-id=\"pt_BR\">Menu de lista</Name><Name language-id=\"ja_JP\"> </Name><Name language-id=\"fr_FR\">Menu de Liste</Name><Name language-id=\"ca_ES\">Men de llista</Name><Name language-id=\"nl_NL\">Lijstmenu</Name><Name language-id=\"hu_HU\">Lista men</Name><Name language-id=\"de_DE\">Listenmen</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt navigointivalikon luettelolla.</Description><Description language-id=\"en_US\">Displays a navigation menu with a list.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra un men de navegacin con una lista.</Description><Description language-id=\"iw_IL\">    .</Description><Description language-id=\"pt_BR\">Exibe um menu de navegao com uma lista.</Description><Description language-id=\"ja_JP\"></Description><Description language-id=\"fr_FR\">Affiche un menu de navigation avec une liste.</Description><Description language-id=\"ca_ES\">Mostra un men de navegaci amb una llista.</Description><Description language-id=\"nl_NL\">Geeft een navigatiemenu weer met een lijst.</Description><Description language-id=\"hu_HU\">Megjelent egy navigcis ment egy listval.</Description><Description language-id=\"de_DE\">Zeigt ein Navigationsmen mit einer Liste.</Description></root>','display',NULL,'ftl','<#include \"${templatesPath}/NAVIGATION-MACRO-FTL\" />\n\n<#if !entries?has_content>\n	<#if themeDisplay.isSignedIn()>\n		<div class=\"alert alert-info\">\n			<@liferay.language key=\"there-are-no-menu-items-to-display\" />\n		</div>\n	</#if>\n<#else>\n	<#assign includeAllChildNavItems = false />\n\n	<#if stringUtil.equals(includedLayouts, \"all\")>\n		<#assign includeAllChildNavItems = true />\n	</#if>\n\n	<div aria-label=\"<@liferay.language key=\"site-pages\" />\" class=\"list-menu\">\n		<@buildNavigation\n			branchNavItems=branchNavItems\n			cssClass=\"layouts\"\n			displayDepth=displayDepth\n			includeAllChildNavItems=includeAllChildNavItems\n			navItemLevel=1\n			navItems=entries\n		/>\n	</div>\n</#if>',0,0,30821,NULL,NULL);
INSERT INTO `DDMTemplate` VALUES ('ab799883-801a-3241-8930-5e87f10103df',30823,20135,20099,20103,NULL,20103,NULL,'2019-10-29 11:37:47.338000','2019-10-29 11:37:47.338000',30819,0,30802,'NAV-PILLS-FTL','1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Pills Vaakasuunta</Name><Name language-id=\"en_US\">Pills Horizontal</Name><Name language-id=\"zh_CN\">Pills Horizontal</Name><Name language-id=\"es_ES\">Globos horizontales</Name><Name language-id=\"iw_IL\"> </Name><Name language-id=\"pt_BR\">Pills Horizontal</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Pilules horizontales</Name><Name language-id=\"ca_ES\">Cpsules horitzontals</Name><Name language-id=\"nl_NL\">Horizontale pillen</Name><Name language-id=\"hu_HU\">Vzszintes tablettk</Name><Name language-id=\"de_DE\">Pills horizontal</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt navigointivalikon navigointikohteet pillerein.</Description><Description language-id=\"en_US\">Displays a navigation menu with navigation items shown as pills.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra un men de navegacin con elementos de navegacin que se muestran como globos.</Description><Description language-id=\"iw_IL\">        .</Description><Description language-id=\"pt_BR\">Exibe um menu de navegao com itens de navegao exibidos como pills.</Description><Description language-id=\"ja_JP\"></Description><Description language-id=\"fr_FR\">Affiche un menu de navigation dont les lments sont affichs sous la forme de pilules.</Description><Description language-id=\"ca_ES\">Mostra un men de navegaci amb elements de navegaci que es mostren com a cpsules.</Description><Description language-id=\"nl_NL\">Geeft een navigatiemenu weer met navigatie-items in de vorm van pillen.</Description><Description language-id=\"hu_HU\">Megjelent egy navigcis ment tablettkknt megjelentett navigcis elemekkel.</Description><Description language-id=\"de_DE\">Zeigt ein Navigationsmen mit Navigationselementen als Pills an.</Description></root>','display',NULL,'ftl','<#if !entries?has_content>\n	<#if themeDisplay.isSignedIn()>\n		<div class=\"alert alert-info\">\n			<@liferay.language key=\"there-are-no-menu-items-to-display\" />\n		</div>\n	</#if>\n<#else>\n	<#assign\n		portletDisplay = themeDisplay.getPortletDisplay()\n\n		navbarId = \"navbar_\" + portletDisplay.getId()\n	/>\n\n	<div id=\"${navbarId}\">\n		<ul aria-label=<@liferay.language key=\"site-pages\" />\" class=\"nav nav-pills navbar-site\" role=\"menubar\">\n			<#assign navItems = entries />\n\n			<#list navItems as navItem>\n				<#assign showChildren = (displayDepth != 1) && navItem.hasBrowsableChildren() />\n\n				<#if navItem.isBrowsable() || showChildren>\n					<#assign\n						nav_item_attr_has_popup = \"\"\n						nav_item_caret = \"\"\n						nav_item_css_class = \"lfr-nav-item nav-item\"\n						nav_item_href_link = \"\"\n						nav_item_link_css_class = \"nav-link text-truncate\"\n					/>\n\n					<#if showChildren>\n						<#assign nav_item_attr_has_popup = \"aria-haspopup=\'true\'\" />\n						<#assign nav_item_caret = \'<span class=\"lfr-nav-child-toggle\"><i class=\"icon-caret-down\"></i></span>\' />\n						<#assign\n							nav_item_css_class = \"${nav_item_css_class} dropdown\"\n							nav_item_link_css_class = \"${nav_item_link_css_class} dropdown-toggle\"\n						/>\n					</#if>\n\n					<#if navItem.isBrowsable()>\n						<#assign nav_item_href_link = \"href=\'${navItem.getURL()}\'\" />\n					</#if>\n\n					<#if navItem.isSelected()>\n						<#assign\n							nav_item_css_class = \"${nav_item_css_class} selected active\"\n						/>\n					</#if>\n\n					<li class