INSERT INTO `DDMTemplateVersion` VALUES (30849,20135,20099,20103,NULL,'2019-10-29 11:37:47.495000',30819,0,30847,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Navigoinnin yleiset makrot</Name><Name language-id=\"en_US\">Navigation common macros</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Macros comunes de navegacin</Name><Name language-id=\"iw_IL\">   </Name><Name language-id=\"pt_BR\">Macros de navegao comuns</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Macros de navigation ordinaires</Name><Name language-id=\"ca_ES\">Macros comunes de navegaci</Name><Name language-id=\"nl_NL\">Algemene navigatiemacro\'s</Name><Name language-id=\"hu_HU\">Navigci kzs makri</Name><Name language-id=\"de_DE\">Navigation hufige Makros</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Sislt yleiset navigointivalikkomallien makrot.</Description><Description language-id=\"en_US\">Contains common macros for navigation menu templates.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Contiene macros comunes para plantillas del men de navegacin.</Description><Description language-id=\"iw_IL\">       .</Description><Description language-id=\"pt_BR\">Contm macros comuns para o modelo do menu de navegao.</Description><Description language-id=\"ja_JP\"></Description><Description language-id=\"fr_FR\">Contient les macros ordinaires pour les modles de menu de navigation.</Description><Description language-id=\"ca_ES\">Cont macros comunes per a les plantilles del men de navegaci.</Description><Description language-id=\"nl_NL\">Bevat algemene macro\'s voor navigatiemenusjablonen.</Description><Description language-id=\"hu_HU\">A navigcis men sablonok kzs makrit tartalmazza.</Description><Description language-id=\"de_DE\">Enthlt hufige Makros fr Navigationsmenvorlagen.</Description></root>','ftl','<#macro buildNavigation\n	branchNavItems\n	cssClass\n	displayDepth\n	includeAllChildNavItems\n	navItemLevel\n	navItems\n>\n	<#if navItems?has_content && ((displayDepth == 0) || (navItemLevel <= displayDepth))>\n		<ul class=\"${cssClass} level-${navItemLevel}\">\n			<#list navItems as navItem>\n				<#assign\n					nav_item_css_class = \"lfr-nav-item\"\n				/>\n\n				<#if includeAllChildNavItems || navItem.isInNavigation(branchNavItems)>\n					<#assign nav_item_css_class = \"${nav_item_css_class} open\" />\n				</#if>\n\n				<#if navItem.isSelected()>\n					<#assign\n						nav_item_css_class = \"${nav_item_css_class} selected active\"\n					/>\n				</#if>\n\n				<li class=\"${nav_item_css_class}\">\n					<#if navItem.isBrowsable()>\n						<a class=\"${nav_item_css_class}\" href=\"${navItem.getRegularURL()!\"\"}\" ${navItem.getTarget()}>${navItem.getName()}</a>\n					<#else>\n						${navItem.getName()}\n					</#if>\n\n					<#if includeAllChildNavItems || navItem.isInNavigation(branchNavItems)>\n						<@buildNavigation\n							branchNavItems=branchNavItems\n							cssClass=cssClass\n							displayDepth=displayDepth\n							includeAllChildNavItems=includeAllChildNavItems\n							navItemLevel=(navItemLevel + 1)\n							navItems=navItem.getChildren()\n						/>\n					</#if>\n				</li>\n			</#list>\n		</ul>\n	</#if>\n</#macro>',0,20103,NULL,'2019-10-29 11:37:47.495000');
INSERT INTO `DDMTemplateVersion` VALUES (30852,20135,20099,20103,NULL,'2019-10-29 11:37:47.517000',30819,0,30850,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Jakopainikkeen Pudotusvalikot</Name><Name language-id=\"en_US\">Split Button Dropdowns</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Desplegables de botn de expansin</Name><Name language-id=\"iw_IL\">    </Name><Name language-id=\"pt_BR\">Dividir menus suspensos de boto</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Partager les boutons du menu droulant</Name><Name language-id=\"ca_ES\">Desplegables de bot de divisi</Name><Name language-id=\"nl_NL\">Rolmenu met uitgesplitste knoppen</Name><Name language-id=\"hu_HU\">Osztott gomb legrdl elemei</Name><Name language-id=\"de_DE\">Dropdown mit geteilten Schaltflchen</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt pudotusnavigointivalikon juurivalikon kohdat esitettyn painikkeina.</Description><Description language-id=\"en_US\">Displays a dropdown navigation menu with root menu items shown as buttons.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra un men de navegacin desplegable con elementos de men raz que se muestran como botones.</Description><Description language-id=\"iw_IL\">         .</Description><Description language-id=\"pt_BR\">Exibe um menu de navegao suspenso com itens do menu raiz mostrados como botes.</Description><Description language-id=\"ja_JP\"> </Description><Description language-id=\"fr_FR\">Affiche un menu de navigation droulant dont les lments sont affichs sous la forme de boutons.</Description><Description language-id=\"ca_ES\">Mostra un men de navegaci desplegable els elements de men arrel que es mostren com a botons.</Description><Description language-id=\"nl_NL\">Geeft een rolmenu voor navigatie weer met rootmenu-items in de vorm van knoppen.</Description><Description language-id=\"hu_HU\">Megjelent egy lenyl navigcis ment, amelyben a gykrszint menpontok gombokknt jelennek meg.</Description><Description language-id=\"de_DE\">Zeigt ein Dropdown-Navigationsmen mit Root-Menelementen als Schaltflchen an.</Description></root>','ftl','<#if !entries?has_content>\n	<#if themeDisplay.isSignedIn()>\n		<div class=\"alert alert-info\">\n			<@liferay.language key=\"there-are-no-menu-items-to-display\" />\n		</div>\n	</#if>\n<#else>\n	<#assign\n		portletDisplay = themeDisplay.getPortletDisplay()\n\n		navbarId = \"navbar_\" + portletDisplay.getId()\n	/>\n\n	<div id=\"${navbarId}\">\n		<ul class=\"navbar-site split-button-dropdowns\">\n			<#assign navItems = entries />\n\n			<#list navItems as navItem>\n				<#assign showChildren = (displayDepth != 1) && navItem.hasBrowsableChildren() />\n\n				<#if navItem.isBrowsable() || showChildren>\n					<#assign\n						nav_item_caret = \"\"\n						nav_item_css_class = \"\"\n						nav_item_href_link = \"\"\n					/>\n\n					<#if navItem.isSelected()>\n						<#assign nav_item_css_class = \"active\" />\n					</#if>\n\n					<#if showChildren>\n						<#assign toggle_text>\n							<@liferay.language key=\"toggle\" />\n						</#assign>\n\n						<#assign nav_item_caret = \"<button aria-expanded=\'false\' aria-haspopup=\'true\' class=\'${nav_item_css_class} btn btn-default dropdown-toggle\' data-toggle=\'dropdown\' type=\'button\'><span class=\'caret\'></span><span class=\'sr-only\'>${toggle_text}</span></button>\" />\n					</#if>\n\n					<#if navItem.isBrowsable()>\n						<#assign nav_item_href_link = \"href=\'${navItem.getURL()}\' ${navItem.getTarget()}\" />\n					</#if>\n\n					<li>\n						<a aria-labelledby=\"layout_${navItem.getLayoutId()}\" class=\"${nav_item_css_class} btn btn-default\" ${nav_item_href_link}><span>${navItem.getName()}</span></a>${nav_item_caret}\n\n						<#if showChildren>\n							<ul class=\"child-menu dropdown-menu\" role=\"menu\">\n								<#list navItem.getBrowsableChildren() as childNavigationItem>\n									<#assign\n										nav_child_css_class = \"\"\n									/>\n\n									<#if childNavigationItem.isSelected()>\n										<#assign\n											nav_child_css_class = \"active\"\n										/>\n									</#if>\n\n									<li class=\"${nav_child_css_class}\" id=\"layout_${childNavigationItem.getLayoutId()}\" role=\"presentation\">\n										<a aria-labelledby=\"layout_${childNavigationItem.getLayoutId()}\" href=\"${childNavigationItem.getURL()}\" ${childNavigationItem.getTarget()} role=\"menuitem\">${childNavigationItem.getName()}</a>\n									</li>\n								</#list>\n							</ul>\n						</#if>\n					</li>\n				</#if>\n			</#list>\n		</ul>\n	</div>\n\n	<@liferay_aui.script use=\"liferay-navigation-interaction\">\n		var navigation = A.one(\'#${navbarId}\');\n\n		Liferay.Data.NAV_INTERACTION_LIST_SELECTOR = \'.navbar-site\';\n		Liferay.Data.NAV_LIST_SELECTOR = \'.navbar-site\';\n\n		if (navigation) {\n			navigation.plug(Liferay.NavigationInteraction);\n		}\n	</@>\n</#if>',0,20103,NULL,'2019-10-29 11:37:47.517000');
INSERT INTO `DDMTemplateVersion` VALUES (30856,20135,20099,20103,NULL,'2019-10-29 11:37:47.551000',30853,0,30854,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Karuselli</Name><Name language-id=\"en_US\">Carousel</Name><Name language-id=\"zh_CN\">Carousel</Name><Name language-id=\"es_ES\">Carrusel</Name><Name language-id=\"iw_IL\"></Name><Name language-id=\"pt_BR\">Carrossel</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Carrousel</Name><Name language-id=\"ca_ES\">Carrussel</Name><Name language-id=\"nl_NL\">Carrousel</Name><Name language-id=\"hu_HU\">Krhinta</Name><Name language-id=\"de_DE\">Karussel</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt kuvat karusellissa.</Description><Description language-id=\"en_US\">Displays images in a carousel.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra imgenes en un carrusel.</Description><Description language-id=\"iw_IL\">   .</Description><Description language-id=\"pt_BR\">Exibe imagens num carrossel.</Description><Description language-id=\"ja_JP\"></Description><Description language-id=\"fr_FR\">Affiche les images dans un carrousel.</Description><Description language-id=\"ca_ES\">Mostra imatges en format carrusel.</Description><Description language-id=\"nl_NL\">Geeft afbeeldingen weer in een carrousel.</Description><Description language-id=\"hu_HU\">Megjelenti a kpeket krhintaszeren.</Description><Description language-id=\"de_DE\">Darstellung von Bildern in einem Karussell.</Description></root>','ftl','<#if entries?has_content>\n	<style>\n		#<@portlet.namespace />carousel .carousel-item {\n			background-color: #000;\n			height: 250px;\n			overflow: hidden;\n			text-align: center;\n			width: 700px;\n		}\n\n		#<@portlet.namespace />carousel .carousel-item img {\n			max-height: 250px;\n			max-width: 700px;\n		}\n	</style>\n\n	<div id=\"<@portlet.namespace />carousel\">\n		<#assign imageMimeTypes = propsUtil.getArray(\"dl.file.entry.preview.image.mime.types\") />\n\n		<#list entries as entry>\n			<#if imageMimeTypes?seq_contains(entry.getMimeType())>\n				<div class=\"carousel-item image-viewer-base-image\">\n					<img src=\"${dlUtil.getPreviewURL(entry, entry.getFileVersion(), themeDisplay, \"\")}\" />\n				</div>\n			</#if>\n		</#list>\n	</div>\n\n	<@liferay_aui.script use=\"aui-carousel\">\n		var carousel = new A.Carousel(\n			{\n				after: {\n					responsive: function(event) {\n						event.stopImmediatePropagation();\n\n						var boundingBox = event.currentTarget.get(\'boundingBox\');\n\n						boundingBox.all(\'.image-viewer-base-image-list, .image-viewer-base-image\').setStyles(\n							{\n								height: \'auto\',\n								maxHeight: event.height,\n								maxWidth: event.width,\n								width: \'auto\'\n							}\n						);\n					}\n				},\n\n				contentBox: \'#<@portlet.namespace />carousel\',\n				height: 250,\n				intervalTime: 2,\n				width: 700\n			}\n		).render();\n	</@liferay_aui.script>\n</#if>',0,20103,NULL,'2019-10-29 11:37:47.551000');
INSERT INTO `DDMTemplateVersion` VALUES (30859,20135,20099,20103,NULL,'2019-10-29 11:37:47.570000',20013,0,30857,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Usean sarakkeen asettelu</Name><Name language-id=\"en_US\">Multi Column Layout</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Diseo con varias columnas</Name><Name language-id=\"iw_IL\">  </Name><Name language-id=\"pt_BR\">Layout em Mltiplas Colunas</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Mise en page multi-colonne</Name><Name language-id=\"ca_ES\">Plana multi-columna</Name><Name language-id=\"nl_NL\">Lay-out met meerdere kolommen</Name><Name language-id=\"hu_HU\">Tbb oszlopos elrendezs</Name><Name language-id=\"de_DE\">Multi-Spalten-Layout</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt sarakkeen jokaiselle sanastolle. Jokainen sarake sislt sanaston nimen, miss sanaston yltason luokitukset on listattu.</Description><Description language-id=\"en_US\">Displays a column for each vocabulary. Each column includes the name of a vocabulary with the vocabulary\'s top level categories listed underneath.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Despliega una columna para cada vocabulario. Cada columna incluye el nombre de un vocabulario con las categoras de mayor nivel del vocabulario listadas debajo.</Description><Description language-id=\"iw_IL\">     .            .</Description><Description language-id=\"pt_BR\">Exibe uma coluna para cada vocabulrio. Cada coluna inclui o nome do vocabulrio com as categorias de nvel mais alto listadas abaixo dele.</Description><Description language-id=\"ja_JP\"></Description><Description language-id=\"fr_FR\">Affiche une colonne pour chaque vocabulaire. Chaque colonne inclut le nom d\'un vocabulaire avec les catgories du niveau racine listes en dessous.</Description><Description language-id=\"ca_ES\">Visualitza una columna per a cada vocabulari. Cada columna inclou el nom del vocabulari amb la categoria de nivell ms alta a sota.</Description><Description language-id=\"nl_NL\">Toon een kolom voor ieder woordenboek. Iedere kolom bevat de naam van een woordenboek met de hoofd categorien eronder.</Description><Description language-id=\"hu_HU\">Megjelent egy oszlopot minden sztr szmra. Minden oszlop tartalmazza a sztr nevt, alatta a sztr fels szint kategriival.</Description><Description language-id=\"de_DE\">Stellt eine Spalte fr jedes Vokabular dar. Jede Spalte enthlt den Namen eines Vokabulars mit der obersten Kategorienebene darunter.</Description></root>','ftl','<#if entries?has_content>\n	<@liferay_aui.row>\n		<#list entries as entry>\n			<@liferay_aui.col width=25>\n				<div class=\"results-header\">\n					<h3>\n						${entry.getUnambiguousTitle(entries, themeDisplay.getSiteGroupId(), themeDisplay.getLocale())}\n					</h3>\n				</div>\n\n				<#assign categories = entry.getCategories() />\n\n				<@displayCategories categories=categories />\n			</@liferay_aui.col>\n		</#list>\n	</@liferay_aui.row>\n</#if>\n\n<#macro displayCategories\n	categories\n>\n	<#if categories?has_content>\n		<ul class=\"categories\">\n			<#list categories as category>\n				<li>\n					<#assign categoryURL = renderResponse.createRenderURL() />\n\n					${categoryURL.setParameter(\"resetCur\", \"true\")}\n					${categoryURL.setParameter(\"categoryId\", category.getCategoryId()?string)}\n\n					<a href=\"${categoryURL}\">${category.getName()}</a>\n\n					<#if serviceLocator??>\n						<#assign\n							assetCategoryService = serviceLocator.findService(\"com.liferay.asset.kernel.service.AssetCategoryService\")\n\n							childCategories = assetCategoryService.getChildCategories(category.getCategoryId())\n						/>\n\n						<@displayCategories categories=childCategories />\n					</#if>\n				</li>\n			</#list>\n		</ul>\n	</#if>\n</#macro>',0,20103,NULL,'2019-10-29 11:37:47.570000');
INSERT INTO `DDMTemplateVersion` VALUES (30862,20135,20099,20103,NULL,'2019-10-29 11:37:47.597000',20017,0,30860,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Vri suosion mukaan</Name><Name language-id=\"en_US\">Color by Popularity</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Color por popularidad</Name><Name language-id=\"iw_IL\">  </Name><Name language-id=\"pt_BR\">Colorir por Popularidade</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Couleur par popularit</Name><Name language-id=\"ca_ES\">Color per popularitat</Name><Name language-id=\"nl_NL\">Kleur op populariteit</Name><Name language-id=\"hu_HU\">Szn npszersg alapjn</Name><Name language-id=\"de_DE\">Nach Popularitt frben</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt sislttageja, mitk on vritetty suosion mukaan: punainen (korkea), keltainen (keskiverto) ja virhe (alhainen)</Description><Description language-id=\"en_US\">Displays asset tags colored by popularity: red (high), yellow (medium), and green (low).</Description><Description language-id=\"zh_CN\">  (),  (),  ().</Description><Description language-id=\"es_ES\">Muestra las etiquetas de contenido coloreadas segn su popularidad : rojo (alta), amarilla (media) y verde (baja).</Description><Description language-id=\"iw_IL\">        :  (),  ()  ().</Description><Description language-id=\"pt_BR\">Exibe marcadores coloridos por popularidade: vermelho (alta), amarelo (mdia) e verde (baixa).</Description><Description language-id=\"ja_JP\">:())</Description><Description language-id=\"fr_FR\">Affiche les tiquettes d\'asset colories par popularit: rouge (lev), jaune (moyen), et vert (bas).</Description><Description language-id=\"ca_ES\">Mostra les etiquetes per colors segons popularitat: vermell (alta), groga (mitjana) i verda (baixa).</Description><Description language-id=\"nl_NL\">Toon content labels volgens populariteit: rood (hoog), geel (midden) en groen (laag).</Description><Description language-id=\"hu_HU\">Megjelenti a tartalom cmkket npszersg szerint sznezve: vrs (magas), srga (kzepes), s zld (alacsony).</Description><Description language-id=\"de_DE\">Schlagwrter nach Popularitt frben: Rot (hoch), gelb (mittel), grn (niedrig).</Description></root>','ftl','<#if entries?has_content>\n	<ul class=\"tag-items tag-list\">\n		<#assign\n			scopeGroupId = getterUtil.getLong(scopeGroupId, themeDisplay.getScopeGroupId())\n			classNameId = getterUtil.getLong(classNameId, 0)\n\n			maxCount = 1\n			minCount = 1\n		/>\n\n		<#list entries as entry>\n			<#assign\n				maxCount = liferay.max(maxCount, entry.getAssetCount())\n				minCount = liferay.min(minCount, entry.getAssetCount())\n			/>\n		</#list>\n\n		<#assign multiplier = 1 />\n\n		<#if maxCount != minCount>\n			<#assign multiplier = 3 / (maxCount - minCount) />\n		</#if>\n\n		<#list entries as entry>\n			<li class=\"taglib-asset-tags-summary\">\n				<#assign popularity = (maxCount - (maxCount - (entry.getAssetCount() - minCount))) * multiplier />\n\n				<#if popularity < 1>\n					<#assign color = \"green\" />\n				<#elseif (popularity >= 1) && (popularity < 2)>\n					<#assign color = \"orange\" />\n				<#else>\n					<#assign color = \"red\" />\n				</#if>\n\n				<#assign tagURL = renderResponse.createRenderURL() />\n\n				${tagURL.setParameter(\"resetCur\", \"true\")}\n				${tagURL.setParameter(\"tag\", entry.getName())}\n\n				<a class=\"tag\" href=\"${tagURL}\" style=\"color: ${color};\">\n					${entry.getName()}\n\n					<#if entry.getAssetCount()?? && getterUtil.getBoolean(showAssetCount)>\n						<span class=\"tag-asset-count\">(${entry.getAssetCount()})</span>\n					</#if>\n				</a>\n			</li>\n		</#list>\n	</ul>\n\n	<br style=\"clear: both;\" />\n</#if>',0,20103,NULL,'2019-10-29 11:37:47.597000');
INSERT INTO `DDMTemplateVersion` VALUES (30865,20135,20099,20103,NULL,'2019-10-29 11:37:47.615000',20044,0,30863,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Usean sarakkeen asettelu</Name><Name language-id=\"en_US\">Multi Column Layout</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Diseo con varias columnas</Name><Name language-id=\"iw_IL\">  </Name><Name language-id=\"pt_BR\">Layout em Mltiplas Colunas</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Mise en page multi-colonne</Name><Name language-id=\"ca_ES\">Plana multi-columna</Name><Name language-id=\"nl_NL\">Lay-out met meerdere kolommen</Name><Name language-id=\"hu_HU\">Tbb oszlopos elrendezs</Name><Name language-id=\"de_DE\">Multi-Spalten-Layout</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt sarakkeen jokaiselle yltason sivulle. Jokainen sarake sislt yltason sivun nimen ja sivun vlittmt alasivut lueteltuna alapuolella.</Description><Description language-id=\"en_US\">Displays a column for each top level page. Each column includes the name of a top level page with the page\'s immediate children listed underneath.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra una columna por cada pgina de nivel superior. En cada columna, se incluye el nombre de una pgina de nivel superior con el elemento secundario inmediato de la pgina en una lista de la parte inferior.</Description><Description language-id=\"iw_IL\">      .                .</Description><Description language-id=\"pt_BR\">Exibe uma coluna para cada pgina de nvel superior. Cada coluna inclui o nome de uma pgina de nvel superior com os filhos imediatos da pgina listados abaixo.</Description><Description language-id=\"ja_JP\"></Description><Description language-id=\"fr_FR\">Affiche une colonne pour chaque page de niveau suprieur. Chaque colonne comprend le nom d\'une page de niveau suprieur avec les enfants immdiats indiqus en-dessous.</Description><Description language-id=\"ca_ES\">Mostra una columna per cada pgina de nivell superior. A cada columna, s\'hi inclou el nom d\'una pgina de nivell superior amb el fill immediat de la pgina en una llista a la part inferior.</Description><Description language-id=\"nl_NL\">Geeft een kolom weer voor elke pagina op het hoogste niveau. Elke kolom bevat de naam van een bovenliggende pagina met alle directe onderliggende pagina\'s eronder.</Description><Description language-id=\"hu_HU\">Megjelent egy oszlopot minden fels szint oldalhoz. Mindegyik oszlop tartalmazza a legfels szint oldal nevt, alatta az oldal kzvetlen utdjval.</Description><Description language-id=\"de_DE\">Zeigt eine Spalte fr jede Seite der obersten Ebene. Jede Spalte enthlt den Namen einer Seite der obersten Ebene, darunter werden die direkten Unterseiten aufgelistet.</Description></root>','ftl','<#if entries?has_content>\n	<@liferay_aui.row>\n		<#list entries as entry>\n			<#if layoutPermission.containsWithoutViewableGroup(permissionChecker, entry, \"VIEW\")>\n				<@liferay_aui.col width=25>\n					<div class=\"results-header\">\n						<h3>\n							<a\n\n							<#assign layoutType = entry.getLayoutType() />\n\n							<#if layoutType.isBrowsable()>\n								href=\"${portalUtil.getLayoutURL(entry, themeDisplay)}\"\n							</#if>\n\n							>${entry.getName(locale)}</a>\n						</h3>\n					</div>\n\n					<@displayPages\n						depth=1\n						pages=entry.getChildren(permissionChecker)\n					/>\n				</@liferay_aui.col>\n			</#if>\n		</#list>\n	</@liferay_aui.row>\n</#if>\n\n<#macro displayPages\n	depth\n	pages\n>\n	<#if pages?has_content && ((depth < displayDepth?number) || (displayDepth?number == 0))>\n		<ul class=\"child-pages\">\n			<#list pages as page>\n				<li>\n					<a\n\n					<#assign pageType = page.getLayoutType() />\n\n					<#if pageType.isBrowsable()>\n						href=\"${portalUtil.getLayoutURL(page, themeDisplay)}\"\n					</#if>\n\n					>${page.getName(locale)}</a>\n\n					<@displayPages\n						depth=depth + 1\n						pages=page.getChildren(permissionChecker)\n					/>\n				</li>\n			</#list>\n		</ul>\n	</#if>\n</#macro>',0,20103,NULL,'2019-10-29 11:37:47.615000');
INSERT INTO `DDMTemplateVersion` VALUES (30869,20135,20099,20103,NULL,'2019-10-29 11:37:47.640000',30866,0,30867,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Kuvake</Name><Name language-id=\"en_US\">Icon</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Icono</Name><Name language-id=\"iw_IL\"></Name><Name language-id=\"pt_BR\">cone</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Icne</Name><Name language-id=\"ca_ES\">Icona</Name><Name language-id=\"nl_NL\">Pictogram</Name><Name language-id=\"hu_HU\">Ikon</Name><Name language-id=\"de_DE\">Symbol</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt kuhunkin kieleen liittyvn lippukuvakkeen.</Description><Description language-id=\"en_US\">Displays the icon of the flag associated with each language.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra el icono de la bandera asociada con cada idioma.</Description><Description language-id=\"iw_IL\">      .</Description><Description language-id=\"pt_BR\">Exibe o cone do marcador associado com cada idioma.</Description><Description language-id=\"ja_JP\"></Description><Description language-id=\"fr_FR\">Affiche l\'icne de chaque option associ  chaque langue.</Description><Description language-id=\"ca_ES\">Mostra la icona de la bandera associada a cada idioma.</Description><Description language-id=\"nl_NL\">Geeft het pictogram weer van de vlag die bij elke taal hoort.</Description><Description language-id=\"hu_HU\">Megjelenti az egyes nyelvekhez trstott zszlt tartalmaz ikont.</Description><Description language-id=\"de_DE\">Zeigt das Symbol der Flagge fr jede Sprache an.</Description></root>','ftl','<#if entries?has_content>\n	<#list entries as entry>\n		<#if entry.isSelected()>\n			<#assign cssClass = \"current-language\" />\n		</#if>\n\n		<#if !entry.isDisabled()>\n			<@liferay_aui[\"icon\"]\n				cssClass=cssClass\n				image=entry.getW3cLanguageId()?lower_case\n				markupView=\"lexicon\"\n				message=entry.getLongDisplayName()\n				url=entry.getURL()\n			/>\n		</#if>\n	</#list>\n</#if>',0,20103,NULL,'2019-10-29 11:37:47.640000');
INSERT INTO `DDMTemplateVersion` VALUES (30872,20135,20099,20103,NULL,'2019-10-29 11:37:47.661000',30866,0,30870,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Kuvakevalikko</Name><Name language-id=\"en_US\">Icon Menu</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Men de icono</Name><Name language-id=\"iw_IL\"> </Name><Name language-id=\"pt_BR\">Menu de cones</Name><Name language-id=\"ja_JP\"> </Name><Name language-id=\"fr_FR\">Menu d\'icnes</Name><Name language-id=\"ca_ES\">Men d\'icones</Name><Name language-id=\"nl_NL\">Pictogrammenu</Name><Name language-id=\"hu_HU\">Ikon men</Name><Name language-id=\"de_DE\">Symbolmen</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt valittuihin kieliin liittyvn kuvakevalikon.</Description><Description language-id=\"en_US\">Displays an icon menu with the selected languages.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra un men de icono con los idiomas seleccionados.</Description><Description language-id=\"iw_IL\">     .</Description><Description language-id=\"pt_BR\">Exibe um menu de cone com os idiomas selecionados.</Description><Description language-id=\"ja_JP\"></Description><Description language-id=\"fr_FR\">Affiche un menu d\'icnes avec les langues slectionnes.</Description><Description language-id=\"ca_ES\">Mostra una icona de men amb els idiomes seleccionats.</Description><Description language-id=\"nl_NL\">Geeft een pictogrammenu weer met de geselecteerde talen.</Description><Description language-id=\"hu_HU\">Megjelent egy ikonment a kivlasztott nyelvekkel.</Description><Description language-id=\"de_DE\">Zeigt ein Symbolmen mit den ausgewhlten Sprachen.</Description></root>','ftl','<#if entries?has_content>\n	<#assign normalizedDefaultLanguageId = stringUtil.toLowerCase(stringUtil.replace(languageId, \"_\", \"-\")) />\n\n	<div class=\"truncate-text\">\n		<@liferay_ui[\"icon-menu\"]\n			direction=\"left-side\"\n			icon=normalizedDefaultLanguageId\n			markupView=\"lexicon\"\n			showWhenSingleIcon=true\n			triggerLabel=normalizedDefaultLanguageId\n			triggerType=\"button\"\n		>\n			<#list entries as entry>\n				<#if !entry.isSelected() && !entry.isDisabled()>\n					<#assign\n						entryLocale = entry.getLocale()\n\n						displayName = entry.getLongDisplayName() + \"-\" + entryLocale.getDisplayCountry(entryLocale)\n						normalizedDefaultLanguageId = stringUtil.toLowerCase(stringUtil.replace(entry.getLanguageId(), \"_\", \"-\"))\n					/>\n\n					<@liferay_ui[\"icon\"]\n						icon=normalizedDefaultLanguageId\n						iconCssClass=\"inline-item inline-item-before\"\n						markupView=\"lexicon\"\n						message=displayName\n						url=entry.getURL()\n					/>\n				</#if>\n			</#list>\n		</@>\n	</div>\n</#if>',0,20103,NULL,'2019-10-29 11:37:47.661000');
INSERT INTO `DDMTemplateVersion` VALUES (30875,20135,20099,20103,NULL,'2019-10-29 11:37:47.676000',30866,0,30873,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Pitk Teksti</Name><Name language-id=\"en_US\">Long Text</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Texto largo</Name><Name language-id=\"iw_IL\"> </Name><Name language-id=\"pt_BR\">Texto Longo</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Texte Long</Name><Name language-id=\"ca_ES\">Text Llarg</Name><Name language-id=\"nl_NL\">Lange tekst</Name><Name language-id=\"hu_HU\">Hossz szveg</Name><Name language-id=\"de_DE\">Langer Text</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt kunkin kielen koko nimen (esim. espanja).</Description><Description language-id=\"en_US\">Displays the full name of each language (e.g. Spanish).</Description><Description language-id=\"zh_CN\">Spanish</Description><Description language-id=\"es_ES\">Muestra el nombre completo de cada idioma (por ejemplo, espaol).</Description><Description language-id=\"iw_IL\">       ( ).</Description><Description language-id=\"pt_BR\">Exibe o nome completo de cada idioma (por exemplo, Espanhol).</Description><Description language-id=\"ja_JP\"> () </Description><Description language-id=\"fr_FR\">Affiche le nom complet de chaque langue (e.x. espagnol).</Description><Description language-id=\"ca_ES\">Mostra el nom sencer de cada idioma (p. ex. espanyol).</Description><Description language-id=\"nl_NL\">Geeft de volledige naam weer van elke taal (bijv. Spaans).</Description><Description language-id=\"hu_HU\">Megjelenti az egyes nyelvek teljes nevt (pl. Spanyol).</Description><Description language-id=\"de_DE\">Zeigt den vollstndigen Namen einer jeden Sprache (z. B. Spanisch).</Description></root>','ftl','<style>\n	.language-entry-long-text {\n		display: inline-block;\n		padding: 0 0.5em;\n	}\n</style>\n\n<#if entries?has_content>\n	<#list entries as entry>\n		<#if !entry.isDisabled()>\n			<@liferay_aui[\"a\"]\n				cssClass=\"language-entry-long-text\"\n				href=entry.getURL()\n				label=entry.getLongDisplayName()\n				lang=entry.getW3cLanguageId()\n			/>\n		</#if>\n	</#list>\n</#if>',0,20103,NULL,'2019-10-29 11:37:47.676000');
INSERT INTO `DDMTemplateVersion` VALUES (30878,20135,20099,20103,NULL,'2019-10-29 11:37:47.692000',30866,0,30876,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Valintalaatikko</Name><Name language-id=\"en_US\">Select Box</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Cuadro de seleccin</Name><Name language-id=\"iw_IL\"> </Name><Name language-id=\"pt_BR\">Caixa de seleo</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Slectionner la case</Name><Name language-id=\"ca_ES\">Selecciona la casella</Name><Name language-id=\"nl_NL\">Selectievak</Name><Name language-id=\"hu_HU\">Jellje be</Name><Name language-id=\"de_DE\">Auswahlkasten</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt valittuihin kieliin liittyvn valintalaatikon.</Description><Description language-id=\"en_US\">Displays a select box with the selected languages.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra una casilla con los idiomas seleccionados.</Description><Description language-id=\"iw_IL\">     .</Description><Description language-id=\"pt_BR\">Exibe uma caixa de seleo com os idiomas selecionados.</Description><Description language-id=\"ja_JP\"></Description><Description language-id=\"fr_FR\">Affiche une bote de slection contenant les langues choisies.</Description><Description language-id=\"ca_ES\">Mostra una casella amb els idiomes seleccionats.</Description><Description language-id=\"nl_NL\">Geeft een selectievak weer met de geselecteerde talen.</Description><Description language-id=\"hu_HU\">Megjelent egy vlasztmezt a kivlasztott nyelvekkel.</Description><Description language-id=\"de_DE\">Zeigt einen Auswahlkasten mit den ausgewhlten Sprachen an.</Description></root>','ftl','<#if entries?has_content>\n	<#assign languageId = localeUtil.toLanguageId(locale) />\n\n	<style>\n		.taglib-language-option {\n			background: none no-repeat 5px center;\n			padding-left: 25px;\n		}\n\n		<#list entries as entry>\n			.taglib-language-option-${entry.getW3cLanguageId()} {\n				background-image: url(${themeDisplay.getPathThemeImages()}/language/${entry.getLanguageId()}.png);\n			}\n		</#list>\n	</style>\n\n	<@liferay_aui[\"form\"]\n		action=formAction\n		method=\"post\"\n		name=\'${namespace + formName}\'\n		useNamespace=false\n	>\n		<@liferay_aui[\"select\"]\n			changesContext=true\n			id=\'${namespace + formName}\'\n			label=\"\"\n			name=\'${name}\'\n			onChange=\'${namespace + \"changeLanguage();\"}\'\n			title=\"language\"\n		>\n			<#list entries as entry>\n				<@liferay_aui[\"option\"]\n					cssClass=\"taglib-language-option taglib-language-option-${entry.getW3cLanguageId()}\"\n					disabled=entry.isDisabled()\n					label=entry.getLongDisplayName()\n					lang=entry.getW3cLanguageId()\n					selected=entry.isSelected()\n					value=entry.getLanguageId()\n				/>\n			</#list>\n		</@>\n	</@>\n\n	<@liferay_aui[\"script\"]>\n		function ${namespace}changeLanguage() {\n			var languageId = AUI.$(document.${namespace + formName}.${name}).val();\n\n			submitForm(document.${namespace + formName});\n		}\n	</@>\n</#if>',0,20103,NULL,'2019-10-29 11:37:47.692000');
INSERT INTO `DDMTemplateVersion` VALUES (30881,20135,20099,20103,NULL,'2019-10-29 11:37:47.707000',30866,0,30879,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Lyhyt Teksti</Name><Name language-id=\"en_US\">Short Text</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Texto corto</Name><Name language-id=\"iw_IL\"> </Name><Name language-id=\"pt_BR\">Texto curto</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Texte Court</Name><Name language-id=\"ca_ES\">Text curt</Name><Name language-id=\"nl_NL\">Korte tekst</Name><Name language-id=\"hu_HU\">Rvid szveg</Name><Name language-id=\"de_DE\">Kurztext</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt jokaisen kielen lyhenteen (esim. ES).</Description><Description language-id=\"en_US\">Displays an abbreviation of each language (e.g. ES).</Description><Description language-id=\"zh_CN\">ES</Description><Description language-id=\"es_ES\">Muestra una abreviatura de cada idioma (por ejemplo, ES).</Description><Description language-id=\"iw_IL\">     ( ES).</Description><Description language-id=\"pt_BR\">Exibe uma abreviatura de cada idioma (por exemplo, ES).</Description><Description language-id=\"ja_JP\"> ( ES) </Description><Description language-id=\"fr_FR\">Affiche une abrviation pour chaque langue (par ex. : ES).</Description><Description language-id=\"ca_ES\">Mostra una abreviatura de cada idioma (p. ex. ES).</Description><Description language-id=\"nl_NL\">Geeft een afkorting weer van elke taal (bijv. ES).</Description><Description language-id=\"hu_HU\">Megjelenti az egyes nyelvek rvidtst (pl. ES).</Description><Description language-id=\"de_DE\">Zeigt eine Abkrzung jeder Sprache an (z. B. ES).</Description></root>','ftl','<style>\n	.language-entry-short-text {\n		padding: 0 0.5em;\n	}\n</style>\n\n<#if entries?has_content>\n	<#list entries as entry>\n		<#if !entry.isDisabled()>\n			<@liferay_aui[\"a\"]\n				cssClass=\"language-entry-short-text\"\n				href=entry.getURL()\n				label=entry.getShortDisplayName()\n				lang=entry.getW3cLanguageId()\n			/>\n		</#if>\n	</#list>\n</#if>',0,20103,NULL,'2019-10-29 11:37:47.707000');
INSERT INTO `DDMTemplateVersion` VALUES (30884,20135,20099,20103,NULL,'2019-10-29 11:37:47.725000',20015,0,30882,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Kartta</Name><Name language-id=\"en_US\">Map</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Mapa</Name><Name language-id=\"iw_IL\"></Name><Name language-id=\"pt_BR\">Mapa</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Carte</Name><Name language-id=\"ca_ES\">Mapa</Name><Name language-id=\"nl_NL\">Kaart</Name><Name language-id=\"hu_HU\">Trkp</Name><Name language-id=\"de_DE\">Landkarte</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt maantieteelliset sisllt kartalla.</Description><Description language-id=\"en_US\">Displays geolocalized assets in a map.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra activos geolocalizados en un mapa.</Description><Description language-id=\"iw_IL\">    -   .</Description><Description language-id=\"pt_BR\">Exibe os contedos geolocalizados em um mapa.</Description><Description language-id=\"ja_JP\"></Description><Description language-id=\"fr_FR\">Affiche les assets golocaliss dans un plan</Description><Description language-id=\"ca_ES\">Mostra contingut geolocalitzat en un mapa.</Description><Description language-id=\"nl_NL\">Toon geolocale content op een kaart.</Description><Description language-id=\"hu_HU\">Megjelenti a fldrajzi tartalmakat egy trkpen.</Description><Description language-id=\"de_DE\">Stellt Assets mit geografischen Informationen in einer Karte dar.</Description></root>','ftl','<#assign\n	defaultLatitude = -3.6833\n	defaultLongitude = 40.40\n\n	group = themeDisplay.getScopeGroup()\n\n	mapsAPIProvider = group.getLiveParentTypeSettingsProperty(\"mapsAPIProvider\")!\"\"\n\n	companyPortletPreferences = prefsPropsUtil.getPreferences(companyId)\n/>\n\n<#if validator.isNull(mapsAPIProvider)>\n	<#assign mapsAPIProvider = companyPortletPreferences.getValue(\"mapsAPIProvider\", \"Google\") />\n</#if>\n\n<#assign featureCollectionJSONObject = jsonFactoryUtil.createJSONObject() />\n\n<@liferay.silently featureCollectionJSONObject.put(\"type\", \"FeatureCollection\") />\n\n<#assign featureJSONArray = jsonFactoryUtil.createJSONArray() />\n\n<#list entries as entry>\n	<#assign\n		assetRenderer = entry.getAssetRenderer()\n\n		ddmFormValuesReader = assetRenderer.getDDMFormValuesReader()\n\n		ddmFormFieldValues = ddmFormValuesReader.getDDMFormFieldValues(\"ddm-geolocation\")\n\n		coordinatesJSONObjects = []\n	/>\n\n	<#list ddmFormFieldValues as ddmFormFieldValue>\n		<#assign\n			value = ddmFormFieldValue.getValue()\n\n			coordinatesJSONObject = jsonFactoryUtil.createJSONObject(value.getString(locale))\n\n			coordinatesJSONObjects = coordinatesJSONObjects + [coordinatesJSONObject]\n		/>\n	</#list>\n\n	<#list coordinatesJSONObjects as coordinatesJSONObject>\n		<#assign featureJSONObject = jsonFactoryUtil.createJSONObject() />\n\n		<@liferay.silently featureJSONObject.put(\"type\", \"Feature\") />\n\n		<#assign geometryJSONObject = jsonFactoryUtil.createJSONObject() />\n\n		<@liferay.silently geometryJSONObject.put(\"type\", \"Point\") />\n\n		<#assign coordinatesJSONArray = [coordinatesJSONObject.getDouble(\"longitude\"), coordinatesJSONObject.getDouble(\"latitude\")] />\n\n		<@liferay.silently geometryJSONObject.put(\"coordinates\", coordinatesJSONArray) />\n\n		<@liferay.silently featureJSONObject.put(\"geometry\", geometryJSONObject) />\n\n		<#assign propertiesJSONObject = jsonFactoryUtil.createJSONObject() />\n\n		<@liferay.silently propertiesJSONObject.put(\"title\", assetRenderer.getTitle(locale)) />\n\n		<#assign entryAbstract>\n			<@getAbstract asset=entry />\n		</#assign>\n\n		<@liferay.silently propertiesJSONObject.put(\"abstract\", entryAbstract) />\n\n		<#if stringUtil.equals(mapsAPIProvider, \"Google\")>\n			<#assign\n				images = {\n					\"com.liferay.document.library.kernel.model.DLFileEntry\": \"${themeDisplay.getProtocol()}://maps.google.com/mapfiles/ms/icons/green-dot.png\",\n					\"com.liferay.portlet.dynamicdatalists.model.DDLRecord\": \"${themeDisplay.getProtocol()}://maps.google.com/mapfiles/ms/icons/red-dot.png\",\n					\"com.liferay.journal.model.JournalArticle\": \"${themeDisplay.getProtocol()}://maps.google.com/mapfiles/ms/icons/blue-dot.png\",\n					\"default\": \"${themeDisplay.getProtocol()}://maps.google.com/mapfiles/ms/icons/yellow-dot.png\"\n				}\n			/>\n\n			<#if images?keys?seq_contains(entry.getClassName())>\n				<@liferay.silently propertiesJSONObject.put(\"icon\", images[entry.getClassName()]) />\n			<#else>\n				<@liferay.silently propertiesJSONObject.put(\"icon\", images[\"default\"]) />\n			</#if>\n		</#if>\n\n		<@liferay.silently featureJSONObject.put(\"properties\", propertiesJSONObject) />\n\n		<@liferay.silently featureJSONArray.put(featureJSONObject) />\n	</#list>\n</#list>\n\n<@liferay.silently featureCollectionJSONObject.put(\"features\", featureJSONArray) />\n\n<style type=\"text/css\">\n	.asset-entry-abstract {\n		min-width: 400px;\n	}\n\n	.leaflet-popup .asset-entry-abstract {\n		display: inline-block;\n	}\n\n	.asset-entry-abstract .asset-entry-abstract-image {\n		display: block;\n		float: left;\n		height: 128px;\n		margin-right: 1em;\n		text-align: center;\n	}\n\n	.asset-entry-abstract .asset-entry-abstract-image img {\n		display: block;\n		margin: 0 auto;\n		max-height: 100%;\n	}\n\n	.asset-entry-abstract .taglib-icon {\n		float: right;\n	}\n</style>\n\n<@liferay_map[\"map-display\"]\n	name=\'Map\'\n	points=\"${featureCollectionJSONObject}\"\n/>\n\n<@liferay_aui.script use=\"liferay-map-base\">\n	Liferay.componentReady(\'<@portlet.namespace />Map\').then(\n		function(map) {\n			map.on(\n				\'featureClick\',\n				function(event) {\n					var feature = event.feature;\n\n					map.openDialog(\n						{\n							content: feature.getProperty(\'abstract\'),\n							marker: feature.getMarker(),\n							position: feature.getGeometry().get(\'location\')\n						}\n					);\n				}\n			);\n		}\n	);\n</@liferay_aui.script>\n\n<#macro getAbstract asset>\n	<div class=\"asset-entry-abstract\" id=\"<@portlet.namespace />assetEntryAbstract\">\n		<#assign\n			showEditURL = paramUtil.getBoolean(renderRequest, \"showEditURL\", true)\n\n			assetRenderer = asset.getAssetRenderer()\n		/>\n\n		<#if showEditURL && assetRenderer.hasEditPermission(permissionChecker)>\n			<#assign redirectURL = renderResponse.createLiferayPortletURL(themeDisplay.getPlid(), themeDisplay.getPortletDisplay().getId(), \"RENDER_PHASE\", false) />\n\n			${redirectURL.setParameter(\"mvcPath\", \"/add_asset_redirect.jsp\")}\n\n			<#assign\n				editPortletURL = assetRenderer.getURLEdit(renderRequest, renderResponse, windowStateFactory.getWindowState(\"POP_UP\"), redirectURL)\n\n				taglibEditURL = \"javascript:Liferay.Util.openWindow({id: \'\" + renderResponse.getNamespace() + \"editAsset\', title: \'\" + htmlUtil.escapeJS(languageUtil.format(locale, \"edit-x\", htmlUtil.escape(assetRenderer.getTitle(locale)), false)) + \"\', uri:\'\" + htmlUtil.escapeJS(editPortletURL.toString()) + \"\'});\"\n			/>\n\n			<@liferay_ui.icon\n				image=\"edit\"\n				label=true\n				message=\"edit\"\n				url=taglibEditURL\n			/>\n		</#if>\n\n		<#if assetRenderer.getThumbnailPath(renderRequest)??>\n			<div class=\"asset-entry-abstract-image\">\n				<img src=\"${assetRenderer.getThumbnailPath(renderRequest)}\" />\n			</div>\n		</#if>\n\n		<#assign assetURL = assetPublisherHelper.getAssetViewURL(renderRequest, renderResponse, assetRenderer, asset, !stringUtil.equals(assetLinkBehavior, \"showFullContent\")) />\n\n		<div class=\"asset-entry-abstract-content\">\n			<h3><a href=\"${assetURL}\">${assetRenderer.getTitle(locale)}</a></h3>\n\n			<div>\n				${assetRenderer.getSummary(renderRequest, renderResponse)}\n			</div>\n		</div>\n\n		<div class=\"asset-entry-abstract-footer\">\n			<a href=\"${assetURL}\">${languageUtil.get(locale, \"read-more\")} &raquo;</a>\n		</div>\n	</div>\n</#macro>',0,20103,NULL,'2019-10-29 11:37:47.725000');
INSERT INTO `DDMTemplateVersion` VALUES (30887,20135,20099,20103,NULL,'2019-10-29 11:37:47.747000',20015,0,30885,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Muotoiltu yhteenveto</Name><Name language-id=\"en_US\">Rich Summary</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Resumen completo</Name><Name language-id=\"iw_IL\"> </Name><Name language-id=\"pt_BR\">Sumrio Rico</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Sommaire enrichi</Name><Name language-id=\"ca_ES\">Resum enriquit</Name><Name language-id=\"nl_NL\">Rijke samenvatting</Name><Name language-id=\"hu_HU\">B sszest</Name><Name language-id=\"de_DE\">Rich-Text-Zusammenfassung</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt lyhenteet, kuvakkeet, liittyvt sisllt ja sisltjen tulosta/muokkaa toiminnot. Voit ottaa mukaan mys sisltjen kirjanmerkit ja arvostelut.</Description><Description language-id=\"en_US\">Displays abstracts, icons, related assets, and print/edit actions for assets. Optionally include asset bookmarks and ratings.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra abstractos, iconos, activos relacionados e imprime/edita acciones para activos. Opcionalmente incluye marcadores de activos y calificaciones.</Description><Description language-id=\"iw_IL\"> , ,    /  .     .</Description><Description language-id=\"pt_BR\">Exibe abstracts, cones, contedos relacionados e aes de imprimir/editar para os contedos. Opcionalmente, inclui marcadores e classificaes de contedos.</Description><Description language-id=\"ja_JP\">/</Description><Description language-id=\"fr_FR\">Affiche les rsums, icons, les assets relatifs, et les actions imprimer/diter pour les assets. Facultativement inclut les signets et les notations d\'assets.</Description><Description language-id=\"ca_ES\">Mostra resums, icones, continguts relacionats i accions d\'impressi o edici per continguts. Opcionalment, inclou enllaos i valoracions.</Description><Description language-id=\"nl_NL\">Toon samenvattingen, pictogrammen, gerelateerde content en afdruk en bewerk acties voor content. Voeg optioneel bladwijzers en beoordelingen toe.</Description><Description language-id=\"hu_HU\">Megjelenti a kivonatokat, ikonokat, a kapcsold tartalmakat, s a nyomtatsi/szerkesztsi mveleteket a tarlamakhoz. Opcionlisan tartalmazza a tartalom knyvjelzket s rtkelseket.</Description><Description language-id=\"de_DE\">Stellt Kurzfassungen, Icons, verwandte Assets sowie Druck- und Bearbeitungsoptionen fr Assets dar. Kann auch Bookmarks und Bewertungen enthalten.</Description></root>','ftl','<#if !entries?has_content>\n	<#if !themeDisplay.isSignedIn()>\n		${renderRequest.setAttribute(\"PORTLET_CONFIGURATOR_VISIBILITY\", true)}\n	</#if>\n\n	<div class=\"alert alert-info\">\n		<@liferay_ui[\"message\"] key=\"there-are-no-results\" />\n	</div>\n</#if>\n\n<#list entries as entry>\n	<#assign\n		entry = entry\n\n		assetRenderer = entry.getAssetRenderer()\n\n		entryTitle = htmlUtil.escape(assetRenderer.getTitle(locale))\n\n		viewURL = assetPublisherHelper.getAssetViewURL(renderRequest, renderResponse, assetRenderer, entry, !stringUtil.equals(assetLinkBehavior, \"showFullContent\"))\n	/>\n\n	<div class=\"asset-abstract\">\n		<div class=\"pull-right\">\n			<@getPrintIcon />\n\n			<@getFlagsIcon />\n\n			<@getEditIcon />\n		</div>\n\n		<h3 class=\"asset-title\">\n			<a href=\"${viewURL}\">\n				${entryTitle}\n			</a>\n		</h3>\n\n		<@getMetadataField fieldName=\"tags\" />\n\n		<@getMetadataField fieldName=\"create-date\" />\n\n		<@getMetadataField fieldName=\"view-count\" />\n\n		<div class=\"asset-content\">\n			<@getSocialBookmarks />\n\n			<div class=\"asset-summary\">\n				<@getMetadataField fieldName=\"author\" />\n\n				${htmlUtil.escape(assetRenderer.getSummary(renderRequest, renderResponse))}\n\n				<a href=\"${viewURL}\"><@liferay.language key=\"read-more\" /><span class=\"hide-accessible\"><@liferay.language key=\"about\" />${entryTitle}</span> &raquo;</a>\n			</div>\n\n			<@getRatings />\n\n			<@getRelatedAssets />\n\n			<@getDiscussion />\n		</div>\n	</div>\n</#list>\n\n<#macro getDiscussion>\n	<#if getterUtil.getBoolean(enableComments) && assetRenderer.isCommentable()>\n		<br />\n\n		<#assign discussionURL = renderResponse.createActionURL() />\n\n		${discussionURL.setParameter(\"javax.portlet.action\", \"invokeTaglibDiscussion\")}\n\n		<@liferay_comment[\"discussion\"]\n			className=entry.getClassName()\n			classPK=entry.getClassPK()\n			formAction=discussionURL?string\n			formName=\"fm\" + entry.getClassPK()\n			ratingsEnabled=getterUtil.getBoolean(enableCommentRatings)\n			redirect=currentURL\n			userId=assetRenderer.getUserId()\n		/>\n	</#if>\n</#macro>\n\n<#macro getEditIcon>\n	<#if assetRenderer.hasEditPermission(themeDisplay.getPermissionChecker())>\n		<#assign redirectURL = renderResponse.createRenderURL() />\n\n		${redirectURL.setParameter(\"mvcPath\", \"/add_asset_redirect.jsp\")}\n		${redirectURL.setWindowState(\"pop_up\")}\n\n		<#assign editPortletURL = assetRenderer.getURLEdit(renderRequest, renderResponse, windowStateFactory.getWindowState(\"pop_up\"), redirectURL)!\"\" />\n\n		<#if validator.isNotNull(editPortletURL)>\n			<#assign title = languageUtil.format(locale, \"edit-x\", entryTitle, false) />\n\n			<@liferay_ui[\"icon\"]\n				cssClass=\"icon-monospaced visible-interaction\"\n				icon=\"pencil\"\n				markupView=\"lexicon\"\n				message=title\n				url=\"javascript:Liferay.Util.openWindow({id:\'\" + renderResponse.getNamespace() + \"editAsset\', title: \'\" + title + \"\', uri:\'\" + htmlUtil.escapeURL(editPortletURL.toString()) + \"\'});\"\n			/>\n		</#if>\n	</#if>\n</#macro>\n\n<#macro getFlagsIcon>\n	<#if getterUtil.getBoolean(enableFlags)>\n		<@liferay_flags[\"flags\"]\n			className=entry.getClassName()\n			classPK=entry.getClassPK()\n			contentTitle=entry.getTitle(locale)\n			label=false\n			reportedUserId=entry.getUserId()\n		/>\n	</#if>\n</#macro>\n\n<#macro getMetadataField\n	fieldName\n>\n	<#if stringUtil.split(metadataFields)?seq_contains(fieldName)>\n		<span class=\"metadata-entry metadata-${fieldName}\">\n			<#assign dateFormat = \"dd MMM yyyy - HH:mm:ss\" />\n\n			<#if stringUtil.equals(fieldName, \"author\")>\n				<@liferay.language key=\"by\" /> ${htmlUtil.escape(portalUtil.getUserName(assetRenderer.getUserId(), assetRenderer.getUserName()))}\n			<#elseif stringUtil.equals(fieldName, \"categories\")>\n				<@liferay_asset[\"asset-categories-summary\"]\n					className=entry.getClassName()\n					classPK=entry.getClassPK()\n					portletURL=renderResponse.createRenderURL()\n				/>\n			<#elseif stringUtil.equals(fieldName, \"create-date\")>\n				${dateUtil.getDate(entry.getCreateDate(), dateFormat, locale)}\n			<#elseif stringUtil.equals(fieldName, \"expiration-date\")>\n				${dateUtil.getDate(entry.getExpirationDate(), dateFormat, locale)}\n			<#elseif stringUtil.equals(fieldName, \"modified-date\")>\n				${dateUtil.getDate(entry.getModifiedDate(), dateFormat, locale)}\n			<#elseif stringUtil.equals(fieldName, \"priority\")>\n				${entry.getPriority()}\n			<#elseif stringUtil.equals(fieldName, \"publish-date\")>\n				${dateUtil.getDate(entry.getPublishDate(), dateFormat, locale)}\n			<#elseif stringUtil.equals(fieldName, \"tags\")>\n				<@liferay_asset[\"asset-tags-summary\"]\n					className=entry.getClassName()\n					classPK=entry.getClassPK()\n					portletURL=renderResponse.createRenderURL()\n				/>\n			<#elseif stringUtil.equals(fieldName, \"view-count\")>\n				${entry.getViewCount()} <@liferay.language key=\"views\" />\n			</#if>\n		</span>\n	</#if>\n</#macro>\n\n<#macro getPrintIcon>\n	<#if getterUtil.getBoolean(enablePrint)>\n		<#assign printURL = renderResponse.createRenderURL() />\n\n		${printURL.setParameter(\"mvcPath\", \"/view_content.jsp\")}\n		${printURL.setParameter(\"assetEntryId\", entry.getEntryId()?string)}\n		${printURL.setParameter(\"viewMode\", \"print\")}\n		${printURL.setParameter(\"type\", entry.getAssetRendererFactory().getType())}\n		${printURL.setWindowState(\"pop_up\")}\n\n		<@liferay_ui[\"icon\"]\n			iconCssClass=\"icon-print\"\n			message=\"print\"\n			url=\"javascript:Liferay.Util.openWindow({id:\'\" + renderResponse.getNamespace() + \"printAsset\', title: \'\" + languageUtil.format(locale, \"print-x-x\", [\"hide-accessible\", entryTitle], false) + \"\', uri: \'\" + htmlUtil.escapeURL(printURL.toString()) + \"\'});\"\n		/>\n	</#if>\n</#macro>\n\n<#macro getRatings>\n	<#if getterUtil.getBoolean(enableRatings) && assetRenderer.isRatable()>\n		<div class=\"asset-ratings\">\n			<@liferay_ui[\"ratings\"]\n				className=entry.getClassName()\n				classPK=entry.getClassPK()\n			/>\n		</div>\n	</#if>\n</#macro>\n\n<#macro getRelatedAssets>\n	<#if getterUtil.getBoolean(enableRelatedAssets)>\n		<@liferay_asset[\"asset-links\"]\n			assetEntryId=entry.getEntryId()\n			viewInContext=!stringUtil.equals(assetLinkBehavior, \"showFullContent\")\n		/>\n	</#if>\n</#macro>\n\n<#macro getSocialBookmarks>\n	<@liferay_social_bookmarks[\"bookmarks\"]\n		className=entry.getClassName()\n		classPK=entry.getClassPK()\n		displayStyle=\"${socialBookmarksDisplayStyle}\"\n		target=\"_blank\"\n		title=entry.getTitle(locale)\n		url=viewURL\n	/>\n</#macro>',0,20103,NULL,'2019-10-29 11:37:47.747000');
INSERT INTO `DDMTemplateVersion` VALUES (33407,20135,20099,20103,NULL,'2019-10-29 11:38:07.905000',27901,0,33405,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Name language-id=\"fi_FI\">Perus</Name><Name language-id=\"en_US\">Basic</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Bsico</Name><Name language-id=\"iw_IL\"></Name><Name language-id=\"pt_BR\">Bsico</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Basique</Name><Name language-id=\"ca_ES\">Bsic</Name><Name language-id=\"nl_NL\">Basis</Name><Name language-id=\"hu_HU\">Egyszer</Name><Name language-id=\"de_DE\">Einfach</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,fi_FI,zh_CN,es_ES,iw_IL,pt_BR,ja_JP,fr_FR,ca_ES,nl_NL,hu_HU,de_DE\" default-locale=\"en_US\"><Description language-id=\"fi_FI\">Nytt blogien otsikot, tekijt ja lyhenteet lyhyesti</Description><Description language-id=\"en_US\">Displays titles, authors, and abstracts compactly for blog entries.</Description><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra de forma compacta ttulos, autores y resmenes para las entradas del blog.</Description><Description language-id=\"iw_IL\"> ,       .</Description><Description language-id=\"pt_BR\">Exibe ttulos, autores e resumos compactamente para posts de blog.</Description><Description language-id=\"ja_JP\"></Description><Description language-id=\"fr_FR\">Affiche titres, auteurs, et abstraits de manire compacte pour les entres de blog.</Description><Description language-id=\"ca_ES\">Mostra els titles, autors i resums de forma compacta per les entrades del blog.</Description><Description language-id=\"nl_NL\">Geeft titels, auteurs en samenvatting compact weer voor blogs.</Description><Description language-id=\"hu_HU\">Megjelenti kompakt mdon a cmeket, szerzket s kivonatokat a blog bejegyzsekhez.</Description><Description language-id=\"de_DE\">Kompakte Darstellung von Titel, Autoren und Kurzfassungen fr Blogeintrge.</Description></root>','ftl','<div class=\"widget-mode-simple\">\n	<#if entries?has_content>\n		<#list entries as curBlogEntry>\n			<div class=\"widget-mode-simple-entry\">\n				<div class=\"autofit-row widget-topbar\">\n					<div class=\"autofit-col autofit-col-expand\">\n						<#assign viewEntryPortletURL = renderResponse.createRenderURL() />\n\n						${viewEntryPortletURL.setParameter(\"mvcRenderCommandName\", \"/blogs/view_entry\")}\n\n						<#if validator.isNotNull(curBlogEntry.getUrlTitle())>\n							${viewEntryPortletURL.setParameter(\"urlTitle\", curBlogEntry.getUrlTitle())}\n						<#else>\n							${viewEntryPortletURL.setParameter(\"entryId\", curBlogEntry.getEntryId()?string)}\n						</#if>\n\n						<h3 class=\"title\">\n							<a class=\"title-link\" href=\"${viewEntryPortletURL.toString()}\">${blogsEntryUtil.getDisplayTitle(resourceBundle, curBlogEntry)}</a>\n						</h3>\n					</div>\n\n					<div class=\"autofit-col visible-interaction\">\n						<div class=\"dropdown dropdown-action\">\n							<@liferay_ui[\"icon-menu\"]\n								direction=\"left-side\"\n								icon=\"\"\n								markupView=\"lexicon\"\n								message=\"\"\n								showWhenSingleIcon=true\n							>\n								<#if blogsEntryPermission.contains(permissionChecker, curBlogEntry, \"UPDATE\")>\n									<#assign editEntryPortletURL = renderResponse.createRenderURL() />\n\n									${editEntryPortletURL.setWindowState(windowStateFactory.getWindowState(\"MAXIMIZED\"))}\n									${editEntryPortletURL.setParameter(\"mvcRenderCommandName\", \"/blogs/edit_entry\")}\n									${editEntryPortletURL.setParameter(\"redirect\", currentURL)}\n									${editEntryPortletURL.setParameter(\"entryId\", curBlogEntry.getEntryId()?string)}\n\n									<@liferay_ui[\"icon\"]\n										label=true\n										message=\"edit\"\n										url=editEntryPortletURL.toString()\n									/>\n								</#if>\n								<#if blogsEntryPermission.contains(permissionChecker, curBlogEntry, \"PERMISSIONS\")>\n									<#assign permissionsEntryURL = permissionsURLTag.doTag(null, \"com.liferay.blogs.model.BlogsEntry\", blogsEntryUtil.getDisplayTitle(resourceBundle, curBlogEntry), curBlogEntry.getGroupId()?string, curBlogEntry.getEntryId()?string, windowStateFactory.getWindowState(\"POP_UP\").toString(), null, request) />\n\n									<@liferay_ui[\"icon\"]\n										label=true\n										message=\"permissions\"\n										method=\"get\"\n										url=permissionsEntryURL\n										useDialog=true\n									/>\n								</#if>\n								<#if blogsEntryPermission.contains(permissionChecker, curBlogEntry, \"DELETE\")>\n									<#assign deleteEntryPortletURL = renderResponse.createActionURL() />\n\n									${deleteEntryPortletURL.setParameter(\"javax.portlet.action\", \"/blogs/edit_entry\")}\n									${deleteEntryPortletURL.setParameter(\"cmd\", trashHelper.isTrashEnabled(themeDisplay.getScopeGroupId())?then(\"move_to_trash\", \"delete\"))}\n									${deleteEntryPortletURL.setParameter(\"redirect\", currentURL)}\n									${deleteEntryPortletURL.setParameter(\"entryId\", curBlogEntry.getEntryId()?string)}\n\n									<@liferay_ui[\"icon-delete\"]\n										label=true\n										trash=trashHelper.isTrashEnabled(themeDisplay.getScopeGroupId())\n										url=deleteEntryPortletURL.toString()\n									/>\n								</#if>\n							</@>\n						</div>\n					</div>\n				</div>\n\n				<div class=\"autofit-row widget-metadata\">\n					<div class=\"autofit-col inline-item-before\">\n						<@liferay_ui[\"user-portrait\"]\n							userId=curBlogEntry.userId\n							userName=curBlogEntry.userName\n						/>\n					</div>\n\n					<div class=\"autofit-col autofit-col-expand\">\n						<div class=\"autofit-row\">\n							<div class=\"autofit-col autofit-col-expand\">\n								<#if serviceLocator??>\n									<#assign\n										userLocalService = serviceLocator.findService(\"com.liferay.portal.kernel.service.UserLocalService\")\n\n										entryUser = userLocalService.fetchUser(curBlogEntry.getUserId())\n									/>\n\n									<#if entryUser?? && !entryUser.isDefaultUser()>\n										<#assign entryUserURL = entryUser.getDisplayURL(themeDisplay) />\n									</#if>\n								</#if>\n\n								<div class=\"text-truncate-inline\">\n									<a href=\"${(entryUserURL?? && validator.isNotNull(entryUserURL))?then(entryUserURL, \"\")}\" class=\"text-truncate username\">${curBlogEntry.getUserName()}</a>\n								</div>\n\n								<div>\n									${dateUtil.getDate(curBlogEntry.getStatusDate(), \"dd MMM\", locale)}\n\n									<#if blogsPortletInstanceConfiguration.enableReadingTime()>\n										- <@liferay_reading_time[\"reading-time\"] displayStyle=\"simple\" model=curBlogEntry />\n									</#if>\n\n									<#if serviceLocator??>\n										<#assign\n											assetEntryLocalService = serviceLocator.findService(\"com.liferay.asset.kernel.service.AssetEntryLocalService\")\n\n											assetEntry = assetEntryLocalService.getEntry(\"com.liferay.blogs.model.BlogsEntry\", curBlogEntry.getEntryId())\n										/>\n\n										<#if blogsPortletInstanceConfiguration.enableViewCount()>\n											- <@liferay_ui[\"message\"] arguments=assetEntry.getViewCount() key=(assetEntry.getViewCount()==0)?then(\"x-view\", \"x-views\") />\n										</#if>\n									</#if>\n								</div>\n							</div>\n						</div>\n					</div>\n				</div>\n\n				<div class=\"widget-content\">\n					<p class=\"widget-resume\">${stringUtil.shorten(htmlUtil.stripHtml(curBlogEntry.getContent()), 400)}</p>\n\n					<div class=\"autofit-float autofit-row autofit-row-center widget-toolbar\">\n						<#if blogsPortletInstanceConfiguration.enableComments()>\n							<div class=\"autofit-col\">\n								<#assign viewCommentsPortletURL = renderResponse.createRenderURL() />\n\n								${viewCommentsPortletURL.setParameter(\"mvcRenderCommandName\", \"/blogs/view_entry\")}\n	